        public static int FindBrute(char[] text, char[] pattern)
        {
            int n = text.Length;
            int m = pattern.Length;
            var scopeText = n - m;
            for (int i = 0; i <= scopeText; i++)
            {
                int k = 0;
                while (k < m && text[i + k] == pattern[k])
                {
                    k++;
                    if (k == m)
                    {
                        return i;
                    }
                }
            }

            return -1;
        }

    

        public static int FindBoyerMoore2(char[] text, char[] pattern)
        {
            int n = text.Length;
            int m = pattern.Length;
            if (m == 0) return 0;
          
           // Dictionary<char, int> last = new Dictionary<char, int>();
            var list = new List<Tuple<char, int>>();

            for (int jj = 0; jj < n; jj++)
            {
                /* if (!last.Keys.Contains(text[jj]))
                      last.Add(text[jj], -1);*/

                var item = new Tuple<char, int>(text[jj],-1);
                list.Add(item);
            }

            // The formula for every letter in the needle (except the last) is lenght - index - 1
            for (int ii = 0; ii < m; ii++)
            {
                list.Add(new Tuple<char, int>(pattern[ii], ii));
             //   list.Add(item);
            }
          
            int i = m - 1;
            int k = m - 1;

            while (i < n)
            {
                if (text[i] == pattern[k])
                { // a matching character
                    if (k == 0)
                    {
                        return i; // entire pattern has been found
                    }
                    i--; // otherwise, examine previous
                    k--; // characters of text/pattern
                }
                else {
                    int x = i;//list.Where(mx => mx.Item1 == text[i]).LastOrDefault().Item2;
                    i += m - Math.Min(k, 1 + x); // case analysis for jump step
                    k = m - 1; // restart at end of pattern
                }
            }


            return -1;
        }

        public static int FindBoyerMoore(string haystack, string needle)
        {
            // If substring is bigger than string, no match exists
            if (needle.Length > haystack.Length)
                return -1;

            // If the substring has characters not in the string, no match exists
            if (needle.Except(haystack).Any())
                return -1;

            Dictionary<char, int> BadMatchTable = new Dictionary<char, int>();
        
            // Initializes every letter with the default value
            foreach (char c in haystack)
            {
                if (!BadMatchTable.Keys.Contains(c))
                    BadMatchTable.Add(c, needle.Length);
            }

            // The formula for every letter in the needle (except the last) is lenght - index - 1
            for (int i = 0; i < needle.Length - 1; i++)
            {
                BadMatchTable[needle[i]] = needle.Length - i - 1;
            }

            int index = 0;

            while (index <= haystack.Length - needle.Length)
            {
                bool match = true;

                for (int i = needle.Length - 1; i >= 0; i--)
                {
                    if (needle[i] != haystack[index + i])
                    {
                        match = false;
                        index += BadMatchTable[haystack[index + needle.Length - 1]];
                    }
                }

                if (match)
                {
                    return index;
                }
            }

            return -1;
        }





        public static int FindKMP(char[] text, char[] pattern)
        {
            int n = text.Length;
            int m = pattern.Length;
            if (m == 0) return 0; // trivial search for empty string
            int[] fail = ComputeFailKMP(pattern); // computed by private utility
            int j = 0; // index into text
            int k = 0; // index into pattern
            while (j < n)
            {
                if (text[j] == pattern[k])
                { // pattern[0..k] matched thus far
                    if (k == m - 1) return j - m + 1; // match is complete
                    j++; // otherwise, try to extend match
                    k++;
                }
                else if (k > 0)
                    k = fail[k - 1]; // reuse suffix of P[0..k-1]
                else
                    j++;
            }
            return -1; // reached end without match
        }

        public static int[] ComputeFailKMP(char[] pattern)
        {
            int m = pattern.Length;
            int[] fail = new int[m]; // by default, all overlaps are zero
            int j = 1;
            int k = 0;
            while (j < m)
            { // compute fail[j] during this pass, if nonzero
                if (pattern[j] == pattern[k])
                { // k + 1 characters match thus far
                    fail[j] = k + 1;
                    j++;
                    k++;
                }
                else if (k > 0) // k follows a matching prefix
                    k = fail[k - 1];
                else // no match found starting at j
                    j++;
            }
            return fail;
        }

